import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {
  const [values, setValue] = useState([]);

  function onAdd(inputs) {
    setValue((prevValue) => {
      return [...prevValue, inputs];
    });
  }

  function whenDelete(id) {
    setValue((prevValue) => {
      prevValue.filter((items, index) => {
        return index !== id;
      });
    });
  }

  return (
    <div>
      <Header />
      <CreateArea onHandle={onAdd} />
      {values.map((item, index) => {
        return (
          <Note
            key={index}
            id={index}
            title={item.title}
            content={item.content}
            onDelete={whenDelete}
          />
        );
      })}
      <Footer />
    </div>
  );
}

export default App;
